// Helper function to send CloudFormation response
async function sendResponse(event, status, data) {
  const responseBody = JSON.stringify({
    Status: status,
    Reason: `See details in CloudWatch Log Stream: ${process.env.AWS_LAMBDA_LOG_GROUP_NAME}`,
    PhysicalResourceId: event.LogicalResourceId,
    StackId: event.StackId,
    RequestId: event.RequestId,
    LogicalResourceId: event.LogicalResourceId,
    Data: data
  });

  console.log('Response body:', responseBody);

  const https = require('https');
  const url = require('url');

  return new Promise((resolve, reject) => {
    const parsedUrl = url.parse(event.ResponseURL);
    const options = {
      hostname: parsedUrl.hostname,
      port: 443,
      path: parsedUrl.path,
      method: 'PUT',
      headers: {
        'Content-Type': '',
        'Content-Length': responseBody.length
      }
    };

    const request = https.request(options, (response) => {
      console.log(`Response status code: ${response.statusCode}`);
      resolve();
    });

    request.on('error', (error) => {
      console.error('Error sending response:', error);
      reject(error);
    });

    request.write(responseBody);
    request.end();
  });
}

const AWS = require('aws-sdk');
const workmail = new AWS.WorkMail({ region: process.env.AWS_REGION });

exports.handler = async (event) => {
  console.log('Event:', JSON.stringify(event, null, 2));

  if (event.RequestType === 'Create') {
    try {
      // Create WorkMail organization - don't set DirectoryId
      const createOrgResult = await workmail.createOrganization({
        Alias: event.ResourceProperties.OrganizationName
      }).promise();

      // Now register the domain separately
      try {
        await workmail.registerDomain({
          OrganizationId: createOrgResult.OrganizationId,
          DomainName: event.ResourceProperties.DomainName
        }).promise();
        console.log('Domain registered for organization');
      } catch (domainError) {
        console.error('Error registering domain:', domainError);
        // Continue as domain might be added later manually
      }

      console.log('Organization created with ID:', createOrgResult.OrganizationId);

      return {
        PhysicalResourceId: createOrgResult.OrganizationId,
        Data: {
          OrganizationId: createOrgResult.OrganizationId
        }
      };
    } catch (error) {
      console.error('Error creating WorkMail organization:', error);
      throw error;
    }
  } else if (event.RequestType === 'Delete') {
    try {
      // Only attempt to delete if it's a valid ID pattern
      if (event.PhysicalResourceId && event.PhysicalResourceId.match(/^m-[0-9a-f]{32}$/)) {
        await workmail.deleteOrganization({
          OrganizationId: event.PhysicalResourceId,
          DeleteDirectory: false
        }).promise();
        console.log('Organization deleted');
      } else {
        console.log('Not attempting to delete invalid organization ID:', event.PhysicalResourceId);
      }

      return {
        PhysicalResourceId: event.PhysicalResourceId
      };
    } catch (error) {
      console.error('Error deleting WorkMail organization:', error);
      // Don't throw on delete to allow stack cleanup
      return {
        PhysicalResourceId: event.PhysicalResourceId
      };
    }
  } else { // Update
    return {
      PhysicalResourceId: event.PhysicalResourceId,
      Data: {
        OrganizationId: event.PhysicalResourceId
      }
    };
  }
};

const AWS = require('aws-sdk');

exports.handler = async (event) => {
  console.log('Event:', JSON.stringify(event, null, 2));

  if (event.RequestType === 'Create' || event.RequestType === 'Update') {
    try {
      // Get the secret ARN from the event properties
      const secretArn = event.ResourceProperties.SecretArn;

      // Get the current secret value to preserve all fields
      const secretsManager = new AWS.SecretsManager();
      const currentSecretData = await secretsManager.getSecretValue({
        SecretId: secretArn
      }).promise();

      // Parse the current secret JSON
      let currentSecret = JSON.parse(currentSecretData.SecretString);

      // Update with the properties from the event, but don't overwrite the password
      const updatedSecret = {
        ...currentSecret,
        username: event.ResourceProperties.Username || currentSecret.username,
        host: event.ResourceProperties.Host || currentSecret.host,
        port: parseInt(event.ResourceProperties.Port || currentSecret.port, 10),
        secure: event.ResourceProperties.Secure === 'true' || currentSecret.secure
      };

      // Update the secret (but retain the password that was auto-generated)
      await secretsManager.putSecretValue({
        SecretId: secretArn,
        SecretString: JSON.stringify(updatedSecret)
      }).promise();

      console.log('Secret updated successfully');

      return {
        PhysicalResourceId: secretArn,
        Data: {
          SecretArn: secretArn
        }
      };
    } catch (error) {
      console.error('Error updating secret:', error);
      throw error;
    }
  } else if (event.RequestType === 'Delete') {
    // Not actually deleting the secret, as it might be in use
    // The secret has RemovalPolicy.RETAIN so it will persist
    console.log('Secret will be retained during deletion');
    return {
      PhysicalResourceId: event.PhysicalResourceId
    };
  }
};
